// PdfGenerator.js
import React from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

const PdfGenerator = ({ results }) => {
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text('Resultados do Método de Gauss-Jacobi', 14, 10);
    doc.autoTable({
      head: [['Iteração', 'Resultados']],
      body: results.map((result, index) => [`${index + 1}`, result.map(val => val.toFixed(4)).join(', ')]),
      startY: 20,
    });
    doc.save('resultados_gauss_jacobi.pdf');
  };

  return (
    <button onClick={generatePDF}>Imprimir Resultados</button>
  );
};

export default PdfGenerator;
