// PdfGenerator.js
import React from 'react';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

const PdfGenerator = ({ results }) => {
  const generatePDF = () => {
    const doc = new jsPDF();
    doc.text('Resultados do Método de Gauss-Jacobi', 14, 10);

    // Define as cores que você deseja usar para os blocos
    const colors = ['#FFCCCB', '#90EE90', '#ADD8E6', '#FFD700', '#FFB6C1', '#DDA0DD'];

    results.forEach((result, index) => {
      const yPosition = 30 + (index * 20); // Calcula a posição Y para cada linha
      const xPosition = 14; // Posição X da linha

      // Desenha o retângulo colorido
      doc.setFillColor(colors[index % colors.length]); // Altera a cor do bloco
      doc.rect(xPosition, yPosition, 180, 20, 'F'); // Desenha um retângulo preenchido

      // Adiciona o texto dentro do bloco
      doc.setTextColor(0, 0, 0); // Cor do texto
      doc.text(`Iteração ${index + 1}: ${result.map(val => val.toFixed(8)).join(' | ')}`, xPosition + 5, yPosition + 15);
    });

    doc.save('resultados_gauss_jacobi.pdf');
  };

  return (
    <button onClick={generatePDF}>Imprimir Resultados</button>
  );
};

export default PdfGenerator;
