import React, { useState } from 'react';
import './App.css'; // Certifique-se de que o CSS esteja importado corretamente

function App() {
  const [matrixSize, setMatrixSize] = useState(2);
  const [matrix, setMatrix] = useState([]);
  const [vector, setVector] = useState([]);
  const [tolerance, setTolerance] = useState(0.01);
  const [maxIter, setMaxIter] = useState(100);
  const [solution, setSolution] = useState([]);
  const [iterations, setIterations] = useState(0);

  const handleMatrixChange = (i, j, value) => {
    const newMatrix = [...matrix];
    if (!newMatrix[i]) newMatrix[i] = [];
    newMatrix[i][j] = parseFloat(value) || 0;
    setMatrix(newMatrix);
  };

  const handleVectorChange = (i, value) => {
    const newVector = [...vector];
    newVector[i] = parseFloat(value) || 0;
    setVector(newVector);
  };

  const gaussJacobi = (A, b, x0, tol, max_iter) => {
    const n = b.length;
    let x = [...x0];
    let count = 0;
    for (let k = 0; k < max_iter; k++) {
      const x_new = Array(n).fill(0);
      for (let i = 0; i < n; i++) {
        let s = 0;
        for (let j = 0; j < n; j++) {
          if (j !== i) s += A[i][j] * x[j];
        }
        x_new[i] = (b[i] - s) / A[i][i];
      }
      count++;
      if (Math.max(...x_new.map((val, idx) => Math.abs(val - x[idx]))) < tol) {
        setIterations(count);
        return x_new;
      }
      x = x_new;
    }
    setIterations(count);
    return x;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const x0 = Array(matrixSize).fill(0);
    const sol = gaussJacobi(matrix, vector, x0, tolerance, maxIter);
    setSolution(sol);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Método de Gauss-Jacobi</h1>
      </header>
      <main className="App-main">
        <form className='App-form' onSubmit={handleSubmit}>
          <div className='App-size-matriz'>
            <label className='App-size-matriz-label'>
              Tamanho da Matriz:
            </label>
            <div className='App-size-matriz-numer'>
              <input
                type="number"
                value={matrixSize}
                onChange={(e) => setMatrixSize(parseInt(e.target.value) || 2)}
              />
            </div>
          </div>


          <div className='App-sub-header'>
            <h2>Matriz A</h2>
          </div>
          {Array.from({ length: matrixSize }).map((_, i) => (
            <div key={i}>
              {Array.from({ length: matrixSize }).map((_, j) => (
                <input
                  key={`${i}-${j}`}
                  type="number"
                  onChange={(e) => handleMatrixChange(i, j, e.target.value)}
                  placeholder={`A[${i + 1}][${j + 1}]`}
                />
              ))}
            </div>
          ))}

          <h2>Vetor b</h2>
          {Array.from({ length: matrixSize }).map((_, i) => (
            <input
              key={i}
              type="number"
              onChange={(e) => handleVectorChange(i, e.target.value)}
              placeholder={`b[${i + 1}]`}
            />
          ))}

          <div>
            <label>
              Tolerância:
              <input
                type="number"
                step="0.01"
                value={tolerance}
                onChange={(e) => setTolerance(parseFloat(e.target.value))}
              />
            </label>
          </div>

          <div>
            <label>
              Número Máximo de Iterações:
              <input
                type="number"
                value={maxIter}
                onChange={(e) => setMaxIter(parseInt(e.target.value) || 100)}
              />
            </label>
          </div>

          <button type="submit">Calcular</button>
        </form>

        {solution.length > 0 && (
          <div>
            <h2>Solução Aproximada:</h2>
            <p>{solution.map((val, idx) => `x[${idx + 1}] = ${val.toFixed(4)}`).join(', ')}</p>
            <p>Quantidade de Iterações: {iterations}</p>
          </div>
        )}
      </main>
      <footer className="App-footer">
        <p>Álgebra Linear - BSI UFRA | 2023</p>
      </footer>
    </div>
  );
}

export default App;
